<script setup lang="ts">
  import gsap from 'gsap';
  import { ScrollToPlugin } from 'gsap/all';

  import { computed, onMounted, ref } from 'vue';
  import { useWindowSize } from '@vueuse/core';
  import { ScrollTrigger } from 'gsap/ScrollTrigger';
  import { horizontalLoop } from './horizontal-loop.js';

  const { width } = useWindowSize();
  const isSmallScreen = computed(() => width.value < 640);
  const logos = ref<HTMLElement>(
    document.getElementById('logos') as HTMLElement,
  );

  // gsap.registerPlugin(Observer);
  gsap.registerPlugin(ScrollToPlugin);

  onMounted(() => {
    if (!isSmallScreen.value) {
      const marquees = gsap.utils.toArray('.marquee') as HTMLElement[];

      gsap.registerPlugin(ScrollTrigger);

      // Animate marquees
      marquees.forEach((marquee, index) => {
        gsap.set(marquee, {
          willChange: 'transform',
        });

        const tl = gsap.timeline({
          defaults: {
            ease: 'none',
          },
        });

        tl.fromTo(
          marquee,
          {
            x: -500 * ++index,
          },
          {
            x: 0,
            scrollTrigger: {
              trigger: '#marquee-section',
              start: 'top bottom',
              end: 'bottom top',
              scrub: 1,
              // markers: false,
            },
          },
        );
      });

      // Animate logos
      const logos = gsap.utils.toArray('.logos-marquee div');
      horizontalLoop(logos, {
        repeat: -1,
        paddingRight: 40,
        speed: 0.6,
      });
    }
  });
</script>

<style scoped>
  .logos-marquee div {
    @apply rounded-lg bg-flax-smoke-950 transition-[background-color] duration-300 ease-in-out hover:bg-[#2a2c1e];
  }

  .logos-marquee svg {
    width: var(--heading-1) !important;
    height: var(--heading-1) !important;
  }

  .logos-marquee div svg {
    @apply !m-10 !fill-flax-smoke-200;
  }
</style>
